function HighlightText({
  highlightText,
  children,
  backgroundColor = "yellow",
  color = "inherit"
}) {
  const highlightStyle = {
    backgroundColor: backgroundColor,
    color: color
  };
  let result = children;

  let indexOfStart = children.toString().indexOf(highlightText.toString());

  let indexOfEnd = indexOfStart + highlightText.length;
  let highlightedText = children.toString().substring(indexOfStart, indexOfEnd);
  let textFirstPart = children.toString().slice(0, indexOfStart);
  let textSecondPart = children.toString().slice(indexOfEnd, children.length - 1);

  if (highlightText === highlightedText) {
    result = (
      <>
        {textFirstPart}
        <span style={highlightStyle}>{highlightedText}</span>
        {textSecondPart}
      </>
    );
  }

  return <>{result}</>;
}

export default HighlightText;
