import React, { useState, useEffect } from 'react';
import DateFnsUtils from '@date-io/date-fns';
import { connect } from 'react-redux';
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from '@material-ui/pickers';
import { nullifyDateFromTo } from '../redux/actions';
import { getDateFromTo } from './../redux/actions';

const CustomDatePicker = (props: any) => {

  const { nullifyDateFromTo, getDateFromTo, dateFromTo } = props;

  const [selectedDateFrom, handleDateFromChange] = useState<any>(new Date('2021-01-15'));
  const [selectedDateTo, handleDateToChange] = useState<any>(new Date());

  console.log('selectedDateTo  typeof ', typeof (selectedDateTo))
  console.log('selectedDateTo ', selectedDateTo)



  useEffect(() => {
    let from = selectedDateFrom.toISOString().substr(0, 10);
    let to = selectedDateTo.toISOString().substr(0, 10)
    if (selectedDateFrom && selectedDateTo) {
      getDateFromTo({
        dateFrom: selectedDateFrom.toISOString().substr(0, 10),
        dateTo: selectedDateTo.toISOString().substr(0, 10)
      })
    }
  }, [selectedDateFrom, selectedDateTo]);


  return (
    <MuiPickersUtilsProvider utils={DateFnsUtils} >
      <div className="date-pickers-wrap">
        <KeyboardDatePicker
          autoOk
          variant="inline"
          label="date from"
          format="yyyy-MM-dd"
          value={selectedDateFrom}
          InputAdornmentProps={{ position: "start" }}
          onChange={(date: any) => handleDateFromChange(date)}
        />
        <KeyboardDatePicker
          autoOk
          variant="inline"
          label="date to"
          format="yyyy-MM-dd"
          value={selectedDateTo}
          InputAdornmentProps={{ position: "start" }}
          onChange={(date: any) => handleDateToChange(date)}
        />
      </div>
    </MuiPickersUtilsProvider>
  );
}

const mapStateToProps = (state: any) => {
  return {
    dateFromTo: state.leagues.dateFromTo,
  }
}
const mapDispatchToProps = (dispatch: any) => {
  return {
    getDateFromTo: (respons: object) => dispatch(getDateFromTo(respons)),
    nullifyDateFromTo: () => dispatch(nullifyDateFromTo()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CustomDatePicker)
