import React, { useEffect, useState } from "react";
import { requestLeagues } from "../redux/actions"
import { connect } from "react-redux"
import ItemLeagues from "../components/ItemLeagues";
import Warning from "../components/Warning";
import Loading from "../components/Loading";
import SportsSoccerIcon from '@material-ui/icons/SportsSoccer';
import HighlightText from "../components/HighlightText";
import Search from "../components/Search"



const LeaguesList = (props: any) => {
	const { leagues, loading, requestLeagues, errorMessage } = props;

	// search state
	const [searchValue, setSearchValue] = useState('');
	const [allResult, setAllResult] = useState([]);
	const [displayedResult, setDisplayedResult] = useState([]);

	// end search state

	setDisplayedResult(leagues);

	const searchText = () => {

		const searching = (leagues: any) => {

			let idIndexOf = leagues.id.toLowerCase().indexOf(searchValue.toLowerCase());
			let nameIndexOf = leagues.name.toLowerCase().indexOf(searchValue.toLowerCase());

		}

		// написать что она будет делать
		console.log(" --- То что вернёт поиск --- ");

	}

	const handleSearchValue = (event: any) => {
		setSearchValue(event.target.value)
		if (searchValue.length > 1) {
			searchText(displayedResult)
		} else {
			setDisplayedResult(allResult)
		}
	}



	useEffect(() => {
		requestLeagues()
	}, [])

	if (errorMessage) {
		return <h2 style={{ marginTop: "50px" }}>{errorMessage}</h2>
	}
	if (loading) {
		return <Loading />
	}
	if (!leagues.length) {
		return (
			<Warning />
		)
	}

	return <div className="content">
		<h1><SportsSoccerIcon /> Список Лиг</h1>

		<Search handleSearchValue={handleSearchValue} searchText={searchText} />

		{leagues.map((league: any, index: number) => {
			return (
				<ItemLeagues key={league.id} id={league.id} index={index + 1} name={league.name} />
			)
		})
		}
	</div>
};

const mapStateToProps = (state: any) => ({
	leagues: state.leagues.leagues,
	errorMessage: state.leagues.errorMessage,
	loading: state.app.loading,
	season: state.leagues.season
})

const mapDispatchToProps = (dispatch: any) => {
	return {
		requestLeagues: () => dispatch(requestLeagues())
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(LeaguesList);
